

def minRemoveToMakeValid(s: str):
    str_stk = []
    output = ""
    curr = ""
    for i in s:
        if i == "(":
            str_stk.append("")
        elif i == ")":
            if str_stk != []:
                if len(str_stk) > 1:
                    x = "(" + str_stk.pop() + ")"
                    str_stk[-1] += x
                else:
                    output += "(" + str_stk.pop() + ")"
        else:
            if str_stk == []:
                output += i
            else:
                str_stk[-1] += i
    # for the ones like "(a(b(c)d)", we need to take out the first (, since the stack will not be cleared, due to the first (, we need to combine the stack.
    if str_stk != []:
        output += ''.join(str_stk)

    return output


